import React, { useState } from "react";
import { message } from "antd";
import { useHistory } from "react-router-dom";
import { SET_USER_INFOR } from "../../redux/Constants/userConstant";
import { useDispatch } from "react-redux";
import httpServ from "../../serviceWorker/http.service";
import localStorageServ from "../../serviceWorker/locaStorage.service";
import "./Login.css"
import { useFormik } from 'formik';
import { loginUserAction } from "../../redux/Actions/userAction";


export default function Login() {
    let history = useHistory();
    let dispatch = useDispatch();
    const [eroremail,setErorEmail]=useState('');
    const [erorpass,setErorPass]=useState('');

    const validateEmail=(value)=> {
      if (!value) {
       setErorEmail('Trường này không để rỗng.');
      } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(value)) {
        setErorEmail('Invalid email address');
      }
    }

    const validatePass=(value)=> {
      if (!value) {
       setErorPass('Trường này không để rỗng.');
      } 
    }

    const formik = useFormik({
        initialValues: {
            email: '',
            password: '',
        },
        onSubmit: values => {
          validateEmail(values.email);
          validatePass(values.password);
          dispatch(loginUserAction(values));
        },
      });

    return (
  <form onSubmit={(event)=>{
    event.preventDefault();
    formik.handleSubmit(event);
  }} 
  className="min-h-screen flex items-stretch text-white ">
    <div className="lg:flex w-1/2 object-cover hidden bg-gray-500 bg-no-repeat bg-cover relative items-center" style={{backgroundImage: 'url(https://images.unsplash.com/photo-1577495508048-b635879837f1?ixid=MXwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHw%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=675&q=80)'}}>
            <div className="absolute bg-black opacity-20 inset-0 z-0" />
